with open("frames_cleaned.csv") as g:
    while line := g.readline():
        if "frame" in line:
            continue
        _, frame, x, y = line.split(",")
        ball_location[int(frame)] = (int(x), int(y))

# import video
video = cv2.VideoCapture("2024-02-22-113446.webm")
cv2.namedWindow("Video")

out = cv2.VideoWriter("output.avi",
                      cv2.VideoWriter_fourcc(*"MJPG"), 30, (1280, 720))

ATTRACTION_FORCE = 0.00008
FRICTION = 0.01

model = Model(Location(0, 0, 0), FRICTION,
              ATTRACTION_FORCE, ATTRACTION_FORCE, 92, 41, 1190, 614)


for i in range(int(video.get(cv2.CAP_PROP_FRAME_WIDTH))):
    ret, frame = video.read()
    if not ret:
        break
    cv2.putText(frame, f"Frame: {i}", (10, 30),
                cv2.FONT_HERSHEY_SIMPLEX, 1, (0, 0, 255), 2)
    if ball_location[i] is not None:
        cv2.circle(frame, ball_location[i], 5, (0, 0, 255), -1)
        trajectory = model.update(Location(
            ball_location[i][0], ball_location[i][1], i))
        for i in range(1, len(trajectory.locations)):
            cv2.line(frame, (int(trajectory.locations[i-1].x), int(trajectory.locations[i-1].y)),
                     (int(trajectory.locations[i].x), int(trajectory.locations[i].y)), (0, 255, 0), 2)
    out.write(frame.astype('uint8'))
    # cv2.imshow("Video", frame)
    # if cv2.waitKey(33) & 0xFF == ord('q'):
    #     break

video.release()
out.release()
